<div data-fixed="left" class="fixed-bottom-form clearfix">
    <a href="javascript:void(0);" data-display="hide" class="close">×</a>
    <a href="javascript:void(0);" data-display="show" class="open"><i class=" icon-angle-right"></i></a>
    <div class="g-left pull-left">&nbsp;
        <span class="float-img"><i class="myicon-bottom-float"></i></span>
    </div>
    <div class="g-center pull-left">
        <form action="">
            <div class="form-line">
                <input class="form-control" type="text" placeholder="填写你的名称"/>
                <input class="form-control" type="text" placeholder="填写你的电话"/>
            </div>
            <div class="form-line city">
                <select class="form-control" name="" id="">
                    <option value="">四川</option>
                    <option value="">重庆</option>
                </select>
                <select class="form-control" name="" id="">
                    <option value="">成都</option>
                    <option value="">绵阳</option>
                </select>
                <select class="form-control" name="" id="">
                    <option value="">成都</option>
                    <option value="">绵阳</option>
                </select>
            </div>
        </form>
    </div>
    <div class="g-right pull-right">
        <div class="qrcode">
            <div class="qrcode-cont">
                <span class="myicon-qrcode-hongbao"></span>
                <p>扫一扫&nbsp;关注红包</p>
            </div>
        </div>
        <div class="submit">
            <button type="submit" class="btn btn-danger">立即申请</button>
        </div>
