<!--  begin 顶部报名条  -->
<div class="row head-yuyue-form">
    <form action="">
        <div class="yuyue-form-cont">
            <input class="form-control name" type="text" placeholder="请输入您的姓名"/>
            <input class="form-control phone" type="text" placeholder="请输入您的电话号码"/>
            <select class="form-control" name="" id="">
                <option value="">省份</option>
                <option value="">四川</option>
                <option value="">广东</option>
            </select>
            <select class="form-control" name="" id="">
                <option value="">城市/地区</option>
                <option value="">成都</option>
                <option value="">广州</option>
            </select>
            <div class="cate">
                <label>
                    <input type="radio"/>装&nbsp;修
                </label>
                <label>
                    <input type="radio"/>定制家居
                </label>
            </div>
            <button class="btn submit" class="submit">免费预约</button>
        </div>
    </form>
</div>
<!--  end 顶部报名条  -->

<!--  begin 顶部装修和定制切换  -->
<div class="row head-cate-switch">
    <a class="cate on" href="">装修服务</a>
    <a class="cate" href="">定制家居</a>
</div>
<!--  end 顶部装修和定制切换  -->